(function(){tinymce.PluginManager.requireLangPack('pie');tinymce.create('tinymce.plugins.piePlugin',{init:function(ed,url){ed.addCommand('mcepie',function(){ed.windowManager.open({file:url+'/bin/pie.php',width:800+parseInt(ed.getLang('pie.delta_width',0)),height:800+parseInt(ed.getLang('pie.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('pie',{title:'pie.desc',cmd:'mcepie',image:url+'/img/pie.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('pie',n.nodeName=='IMG');});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'pie plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/pie',version:"1.0"};}});tinymce.PluginManager.add('pie',tinymce.plugins.piePlugin);})();